import "./style.css";
import trash from "../../assets/trash.svg";
import { NoCards } from "../NoCards";

export const Card = ({ expense, all, despesa, out }) => {
  const vertificationClass = (obj, classesRed, classGreen) => {
    if (obj.type === "entrada") {
      return classGreen;
    } else {
      return classesRed;
    }
  };
  const verificationExpenseTrueOrFalse = () => {
    if (expense.length === 0) {
      return false;
    } else {
      return true;
    }
  };
  return (
    <>
      <section className="flex-resume mb-noCard">
        <p className="text">Resumo financeiro</p>
        <div className="flex-buttons width-resume">
          <button className="button-all" onClick={all}>
            Todos
          </button>
          <button className="button-gray text-button" onClick={out}>
            Entradas
          </button>
          <button className="button-gray text-button" onClick={despesa}>
            Sa√≠das
          </button>
        </div>
      </section>

      {verificationExpenseTrueOrFalse() ? (
        <ul>
          {/* Criar condicional */}
          {expense.map((element, index) => {
            return (
              <li
                className={vertificationClass(
                  element,
                  "lista-green mb-card-1 ",
                  "lista mb-card-1 "
                )}
                key={index}
              >
                <div className="ml-card">
                  <p className="title-card ">{element.description}</p>
                  <span className="span-card">{element.type}</span>
                </div>
                <div className="width-card flex-card">
                  <span className="span-card">
                    {element.value.toLocaleString("pt-BR", {
                      style: "currency",
                      currency: "BRL",
                    })}
                  </span>
                  <button className="button-trash">
                    <img src={trash} alt="trash" />
                  </button>
                </div>
              </li>
            );
          })}
        </ul>
      ) : (
        <NoCards />
      )}
    </>
  );
};
