import "./style.css";
import { ButtonGray } from "../ButtonGray";
import { useState } from "react";

export const FinancialSummary = ({ expense, setExpenses }) => {
  const [buttonAll, setButtonAll] = useState(false);
  const [buttonDespesa, setButtonDespesa] = useState(false);
  const [buttonEntradas, setButtonEntradas] = useState(false);

  const verificationExpense = () => {
    if (expense) {
      const enterOrExit = expense.filter((element) => {
        return element.type === "despesa";
      });
      return enterOrExit;
    }
  };

  const handleClick = () => {
    setExpenses(verificationExpense);
  };

  return (
    <section className="flex-resume mb-noCard">
      <p className="text">Resumo financeiro</p>
      <div className="flex-buttons width-resume">
        <button
          className={buttonAll ? "button-all" : "button-gray text-button"}
          onClick={() => {
            handleClick();
            setButtonAll(true);
            setButtonDespesa(false);
            setButtonEntradas(false);
          }}
        >
          Todos
        </button>
        <button
          className={buttonEntradas ? "button-all" : "button-gray text-button"}
          onClick={() => {
            handleClick();
            setButtonAll(false);
            setButtonDespesa(false);
            setButtonEntradas(true);
          }}
        >
          Entradas
        </button>
        <button
          className={buttonDespesa ? "button-all" : "button-gray text-button"}
          onClick={() => {
            handleClick();
            setButtonAll(false);
            setButtonDespesa(true);
            setButtonEntradas(false);
            console.log("oi");
          }}
        >
          Saídas
        </button>
        {/*  <ButtonGray description={"Entradas"} />
        <ButtonGray
          description={"Saídas"}
          exit={verificationExpense()}
          setExpenses={setExpenses}
        /> */}
      </div>
    </section>
  );
};
